
def translator(typeCard):
    if typeCard == "itemBlue":
        return 'B'
    elif typeCard == "itemRed":
        return 'R'
    elif typeCard == "itemGreen":
        return 'G'
    else:
        return 'C'



f = open("cards.txt","r")

doc = f.readlines()
FULLDATA = []
for line in doc:
    #print(line)
    data = line.split("; ")
    #print(line.split("; "))
    dataClean = []
    idCard = int(data[0])
    data[2] = data[2][0:-1]
    cardType = translator(data[2])
    cost = int(data[3])
    attack = int(data[4])
    defense = int(data[5])
    data[6] = data[6][0:-1]
    abilities = data[6]
    hpChange = int(data[7])
    hpChangeOpponent = int(data[8])
    cardDraw = int(data[9])
    dataClean = [idCard,cardType,cost,attack,defense,abilities,hpChange,hpChangeOpponent,cardDraw]
    print(dataClean)
    FULLDATA.append(dataClean)

print(FULLDATA)

